Scripts.OnFinished:
local tex = self:GetParent();
tex:SetAlpha(0);
tex:GetParent().ReusedTexture = tex;

Scripts.OnStop:
local tex = self:GetParent();
tex:SetAlpha(0);
tex:GetParent().ReusedTexture = tex;

Scripts.OnLoad:
self.IsOn = false;
self.Label:SetText(NARCI_BORDER_THEME_BRIGHT);
if not self:GetParent().buttons then
    self:GetParent().buttons = {};  
end
tinsert(self:GetParent().buttons, self);

self.IsOn = false;
self.Label:SetText(NARCI_BORDER_THEME_BRIGHT);
if not self:GetParent().TooltipColors then
    self:GetParent().TooltipColors = {};  
end
tinsert(self:GetParent().TooltipColors, self);

Scripts.OnShow:
if IsAddOnLoaded("DynamicCam") then
    self.Description:SetText(NARCI_CAMERA_SAFE_MODE_DESCRIPTION.."\n"..NARCI_CAMERA_SAFE_MODE_DESCRIPTION_EXTRA);
    self.Tick:Hide();
    self:Disable();
end
self:SetScript("OnShow", nil);

Scripts.OnLoad:
self.IsOn = false;
self.Label:SetText(NARCI_LAYOUT_1)
if not self:GetParent().buttons then
    self:GetParent().buttons = {};  
end
tinsert(self:GetParent().buttons, self)

self.IsOn = false;
self.Label:SetText(Narci.L["Entrance Visual"]);
self.Description:SetText(Narci.L["Entrance Visual Description"]);

self.IsOn = false;
self.Label:SetText(Narci.L["Load on Demand"]);
self.lockedText = Narci.L["Load on Demand Description Disabled"];
self.enabledText = Narci.L["Load on Demand Description On"];
self.disabledText = Narci.L["Load on Demand Description Off"];

self.Label:SetText(NARCI_HOTKEY);
self.keyValue = "SearchRelativesHotkey";
self.defaultKey = "TAB";
self.Description:ClearAllPoints();
self.Description:SetPoint("BOTTOMLEFT", self, "TOPLEFT", 0, 6);

self.IsOn = false;
self.Label:SetText(Narci.L["Find Relatives"]);
self.Description:SetText(Narci.L["Find Related Creatures Description"]);

self.IsOn = false;
self.Label:SetText(Narci.L["Translate Names"]);
self.enabledText = Narci.L["Translate Names Description On"];
self.disabledText = Narci.L["Translate Names Description Off"];

self.IsOn = false;
self.singular = Narci.L["Select A Language"];
self.plural = Narci.L["Select Multiple Languages"];

self.IsOn = false;
self.Label:SetText(NARCI_NEW_ENTRY_PREFIX.. Narci.L["Keep Standing"]);
self.Description:SetText(Narci.L["Keep Standing Description"]);
