Scripts.OnLoad:
self.isInteractiveText = true;
local id = self:GetID();
self.Text:SetText(_G["NARCI_SPLASH_INTERACTIVE_TEXT"..id]);
local RGB = NARCI_COLOR_CYAN_DARK_RGB;
self.Marker:SetColorTexture(RGB[1], RGB[2], RGB[3]);
self:SetHeight(self.Text:GetHeight());

Scripts.OnEnter:
UIFrameFadeIn(self.Highlight, 0.25, self.Highlight:GetAlpha(), 0.5);
if self.onEnterFunc then
    self.onEnterFunc(self);
end

Scripts.OnLeave:
UIFrameFadeOut(self.Highlight, 0.25, self.Highlight:GetAlpha(), 0);
if self.onLeaveFunc then
    self.onLeaveFunc(self);
end

Scripts.OnShow:
C_Timer.After(0.25, function()
    self.Text.scaleIn:Play();
    self.ArrowLeft.flyIn:Play();
    self.ArrowRight.flyIn:Play();
end);

Scripts.OnLoad:
self.id = 1;
local d = self:GetParent():GetParent():GetHeight();
self:SetSize(d, d);
local parent = self:GetParent();
self:ClearAllPoints();
self:SetPoint("CENTER", parent, "RIGHT", -d/2, 0);

Scripts.OnLeave:
self.Icon:SetTexCoord(0.5, 1, 0, 1);
if not self.IsExpanded then
    self.Text.Bling:Play();
end

if self:IsMouseOver() then return end
NarciAPI_FadeFrame(self.CloseButton, 0.25, "OUT");
if not self.LogoButton.IsExpanded then
    NarciAPI_FadeFrame(self.LogoButton.Text, 0.15, "OUT");
end
